export default function Chip( { text, color }){
    const hexToRgba = (hex, opacity) => {
        const bigint = parseInt(hex.replace('#', ''), 16);
        const r = (bigint >> 16) & 255;
        const g = (bigint >> 8) & 255;
        const b = bigint & 255;

        return `rgba(${r}, ${g}, ${b}, ${opacity})`;
    };

    return(
        <div style = { { display : "flex", justifyContent : "center", height : "", backgroundColor : hexToRgba(color, 0.16), color : color, border : `1px solid ${hexToRgba(color, 0.16)}`} }>
            <img src = "https://file.notion.so/f/f/e03c720f-f008-4075-a4d9-2cd311c55b1d/8821fa1f-ac36-4dcc-994a-f9bd4390afc4/fire.gif?table=block&id=168afaf7-b684-8038-8713-d82704b06b6b&spaceId=e03c720f-f008-4075-a4d9-2cd311c55b1d&expirationTimestamp=1742083200000&signature=LJyZlh71dD9km65K6TGiEg3x92Np6gMwSkdL2hqyi1M"/>
            <p className = "chip" style = { { padding : "0px 16px" } }>{ text }</p>
        </div>
    )
}
