import { useState, useEffect } from "react";
import { Link } from "react-router-dom"
import IconButton from "./IconButton"
import { ArrowForward, Stadium, EventAvailable } from '@mui/icons-material';

export default function GameListItem( { id } ){
    const [data, setData] = useState([]);

    const fetchData = async ( id ) => {        
        try {
          const response = await fetch("http://localhost:8000/api/query-a-database-games", {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
            },
            body: JSON.stringify({
              id
            })
          });

          if (!response.ok) {
            throw new Error("Network response was not ok");
          }

          const result = await response.json();

        //   console.log('games', result);
          setData(result);
        } catch (error) {
          console.error("Error fetching data:", error.message);
        }
    };

    useEffect( () => {
        fetchData(id);
    }, []);

    return(
        <>
            { data.map( ( item, index ) => (
                <div key = { index } style = { { display : "flex", width : "100%", height : "80px", alignItems : "center", backgroundColor : "var(--black4)", padding : "8px", marginBottom : "16px"} }>

                    <div style = { { flex : 1, display : "flex", flexDirection : "row", gap : "40px",alignItems : "center" } }>
                        {/* SCORE */}
                        <div style = { { display : "flex", flexDirection : "row", gap : "4px",alignItems : "center" } }>
                            <div className = "icon-container">
                                <img className = "icon" src = "https://seeklogo.com/images/T/toronto-raptors-logo-456EE558CF-seeklogo.com.png"/>
                            </div>

                            <div style = { { backgroundColor : "var(--black8)", padding : "0px 10px"}}>
                                <p className = "number">17 : 11</p>
                            </div>

                            <div className = "icon-container">
                                <img className = "icon" src = "https://seeklogo.com/images/T/toronto-raptors-logo-456EE558CF-seeklogo.com.png"/>
                            </div>
                        </div>

                        {/* DATE */}
                        <div style = { { display : "flex", flexDirection : "row", gap : "4px" } }>
                            <EventAvailable fontSize = "24px"/>
                            <p className = "meta">Dec 07, 2024</p>
                        </div>


                        {/* VENUE */}
                        <div style = { { display : "flex", flexDirection : "row", gap : "4px" } }>
                            <Stadium fontSize = "24px"/>
                            <p className = "meta">PS 35</p>
                        </div>
                    </div>    

                    <Link to = {`/game`}>
                        <IconButton icon = { ArrowForward }/>
                    </Link>  

                </div>
            ))} 
        </>
    )
}
