import { useLocation } from "react-router-dom";

import RecordingVideo from "../components/RecordingVideo";

export default function Game(){
    const location = useLocation();

    const { team, players } = location.state || []; // Ensure safe destructuring

    const teamName = team.properties?.Name.title[0].plain_text;

    return(
        <div className = "page">
            <h1>{ teamName }</h1>
            <p>Start Recording Game for { teamName }</p>

            {/* <VideoRecorder/> */}

            <RecordingVideo/>

            <h2>Playing</h2>

            <hr/>

            <h2>Players ({ players.length })</h2>
            <p className = "meta">Press the player button to timestamp highlight</p>
            {/* SIGN UP */}
            <div style = { { display : "flex", flexDirection : "row", gap : "8px" }}>
                { players.map( ( item, index ) => (
                    <div key = { index } style = { { width: "60px" } }>
                        <div style = { { height : "80px", border : "1px solid var(--black40)", backgroundPosition : "center", backgroundImage : "url(https://www.tottenhamhotspur.com/media/5y3crbxe/firstteam_profiles_mickyvandeven_202425_1.png?anchor=center&mode=crop&quality=90&width=500)", backgroundSize : "cover"} }/>
                        <p className = "number">{ item.properties.back_number.rich_text[0].plain_text }</p>
                        <p>{ item.properties?.Name.title[0].plain_text }</p>
                        <p className = "meta" style = { { whiteSpace: "normal", wordWrap: "break-word", overflowWrap: "break-word" } }>{ item.properties.first_name.rich_text[0].plain_text } { item.properties.last_name.rich_text[0].plain_text }</p>
                    </div>
                ))}
            </div>


        </div>
    )
}
