import { useState, useEffect, useRef, useCallback } from "react"

export default function RecordingVideo({ setLogTimestamp, setUploadTimestamp }) {
    const [recording, setRecording] = useState(false);
    const [duration, setDuration] = useState(0);

    const [videoURL, setVideoURL] = useState(null);
    const [blobData, setBlobData] = useState(null);

    const [uploading, setUploading] = useState(false);
    const [uploadedVideoUrl, setUploadedVideoUrl] = useState(null);

    const [fetchedVideoUrl, setFetchedVideoUrl] = useState(null);

    const streamRef = useRef(null);
    const videoRef = useRef(null);
    const mediaRecorderRef = useRef(null);
    const chunksRef = useRef([]);
    const timerRef = useRef(null);

    // State for player timestamps
    const [timestamps, setTimestamps] = useState([]);
    const [timestampData, setTimestampData] = useState(null);

    const startRecording = async () => {
        try {
            const stream = await navigator.mediaDevices.getUserMedia({ 
                video: true,
                audio: true
            });

            streamRef.current = stream;

            if (videoRef.current) {
                videoRef.current.srcObject = stream;
            }

            const mediaRecorder = new MediaRecorder(stream);

            mediaRecorderRef.current = mediaRecorder;
            chunksRef.current = [];

            mediaRecorder.ondataavailable = (event) => {
                if (event.data.size > 0) {
                    chunksRef.current.push(event.data);
                }
            };

            mediaRecorder.start(1000);
            setRecording(true);
            setTimestamps([]); // reset timestamps
            setTimestampData(null); // reset timestamp data

            setDuration(0);
            timerRef.current = setInterval(() => {
                setDuration((prev) => prev + 1);
            }, 1000);

            mediaRecorder.onstop = () => {
                const blob = new Blob(chunksRef.current, { type: "video/webm" });
                setVideoURL(URL.createObjectURL(blob));
                setBlobData(blob);
                clearInterval(timerRef.current);
            };
        } catch (error) {
            console.log(`Camera access error : ${error.message}`);
        }
    }

    const endRecording = () => {
        if (mediaRecorderRef.current && mediaRecorderRef.current.state !== "inactive") {
            mediaRecorderRef.current.stop();
            streamRef.current?.getTracks().forEach((track) => track.stop());

            setRecording(false);
            clearInterval(timerRef.current);
        }
    }

    const uploadRecording = async () => {
        if (!blobData) {
            console.log(`no blob data!`);
            return;
        }

        try {
            setUploading(true);

            const formData = new FormData();
            formData.append('video', blobData, 'recording.webm');

            console.log("Uploading file of size:", blobData.size);

            const response = await fetch("http://localhost:8000/upload-video", {
                method: "POST",
                body: formData,
            });

            const data = await response.json();

            if (!response.ok) {
                throw new Error(data.error || data.details || `Server error: ${response.status}`);
            }

            setUploadedVideoUrl(data.fileUrl);

            await postUrl("Name of Video", data.fileUrl);

            alert("Video uploaded successfully!");
        } catch (error) {
            console.error("Error during upload:", error);
            console.log(error.message);
        } finally {
            setUploading(false);
        }
    };

    const getVideo = async () => {
        if (!uploadedVideoUrl) {
            alert("No uploaded video URL yet. Upload first!");
            return;
        }

        try {
            const fileKey = uploadedVideoUrl.split("?")[0].split("/").pop();
            console.log("Fetching video for fileKey:", fileKey);

            const response = await fetch(`http://localhost:8000/get-video-url/${fileKey}`);

            const data = await response.json();

            if (!response.ok) {
                throw new Error(data.error || `Server error: ${response.status}`);
            }

            console.log("Got presigned URL:", data.fileUrl);

            setFetchedVideoUrl(data.fileUrl);
        } catch (error) {
            console.error("Error fetching video URL:", error);
        }
    };

    // POST Url to Database
    const postUrl = async (name, url) => {
        try {
            const response = await fetch("http://localhost:8000/api/create-a-page", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({
                    name,
                    url
                }),
            });

            if (!response.ok) {
                throw new Error("Network response was not ok");
            }

            console.log("Url is posted to DB successfully");
        } catch (error) {
            console.error("Error posting url to DB:", error.message);
        }
    };

    // Define logTimestamp as a callback to prevent recreation on every render
    const logTimestamp = useCallback((playerId) => {
        console.log("logTimestamp called with player ID:", playerId, "Recording:", recording);
        if (recording) {
            const newTimestamp = {
                id: playerId,
                timestamp: duration
            };

            setTimestamps(prev => {
                const updatedTimestamps = [...prev, newTimestamp];
                console.log("Updated timestamps:", updatedTimestamps);
                return updatedTimestamps;
            });

            console.log(`üìç Timestamp: ${duration}s for player ${playerId}`);
        } else {
            console.warn("Cannot log timestamp: not recording");
        }
    }, [recording, duration]);

    // Upload all collected timestamps
    const uploadTimestamp = useCallback(() => {
        console.log("uploadTimestamp called, timestamps count:", timestamps.length);
        if (timestamps.length === 0) {
            console.warn("No timestamps to upload!");
            return;
        }

        const jsonData = {
            video_name: "Recording",
            duration: duration,
            timestamps: timestamps // Array of {id, timestamp} objects
        };

        setTimestampData(jsonData);
        console.log("üì§ Uploaded Timestamps:", jsonData);

        // Here you would typically send this data to your server
        // For example:
        // sendTimestampsToServer(jsonData);
    }, [timestamps, duration]);

    // Set the external functions when component mounts or when functions change
    useEffect(() => {
        console.log("Setting external timestamp functions");
        if (setLogTimestamp) {
            console.log("Setting logTimestamp function");
            setLogTimestamp(logTimestamp);
        }
        if (setUploadTimestamp) {
            console.log("Setting uploadTimestamp function");
            setUploadTimestamp(uploadTimestamp);
        }
    }, [setLogTimestamp, setUploadTimestamp, logTimestamp, uploadTimestamp]);

    return(
        <div>
            <div style={{ marginBottom: '10px', padding: '10px', backgroundColor: '#f8f9fa', borderRadius: '4px' }}>
                <button 
                    onClick={startRecording}
                    style={{ padding: '8px', margin: '0 4px', backgroundColor: recording ? '#e74c3c' : '#3498db', color: 'white', border: 'none', borderRadius: '4px' }}
                >
                    Start Recording
                </button>
                <button 
                    onClick={endRecording} 
                    disabled={!recording}
                    style={{ padding: '8px', margin: '0 4px', backgroundColor: '#e74c3c', color: 'white', border: 'none', borderRadius: '4px', opacity: recording ? 1 : 0.5 }}
                >
                    End Recording
                </button>
                <button 
                    onClick={uploadRecording} 
                    disabled={!videoURL || uploading}
                    style={{ padding: '8px', margin: '0 4px', backgroundColor: '#2ecc71', color: 'white', border: 'none', borderRadius: '4px', opacity: (!videoURL || uploading) ? 0.5 : 1 }}
                >
                    {uploading ? "Uploading..." : "Upload Video"}
                </button>
                <button 
                    onClick={getVideo} 
                    disabled={!uploadedVideoUrl}
                    style={{ padding: '8px', margin: '0 4px', backgroundColor: '#9b59b6', color: 'white', border: 'none', borderRadius: '4px', opacity: !uploadedVideoUrl ? 0.5 : 1 }}
                >
                    Get Uploaded Video
                </button>
            </div>

            {recording && (
                <div style={{ padding: '10px', backgroundColor: '#f8f9fa', borderRadius: '4px', marginBottom: '10px' }}>
                    <strong>Recording in progress: </strong> {duration}s
                </div>
            )}

            <div style={{ marginBottom: '10px' }}>
                <h3>Timestamps ({timestamps.length}):</h3>
                {timestamps.length > 0 ? (
                    <div style={{ maxHeight: '150px', overflowY: 'auto', padding: '10px', backgroundColor: '#f8f9fa', borderRadius: '4px' }}>
                        {timestamps.map((time, index) => (
                            <p key={index}>‚è± Player ID: {time.id}, Time: {time.timestamp}s</p>
                        ))}
                    </div>
                ) : (
                    <p>No timestamps recorded yet</p>
                )}

                <button 
                    onClick={uploadTimestamp}
                    disabled={timestamps.length === 0}
                    style={{ padding: '8px', margin: '10px 0', backgroundColor: '#f39c12', color: 'white', border: 'none', borderRadius: '4px', opacity: timestamps.length === 0 ? 0.5 : 1 }}
                >
                    Upload All Timestamps
                </button>
            </div>

            {timestampData && (
                <div style={{ marginBottom: '10px', padding: '10px', backgroundColor: '#f8f9fa', borderRadius: '4px' }}>
                    <h3>Timestamp Data (JSON):</h3>
                    <pre style={{ overflowX: 'auto', padding: '10px', backgroundColor: '#f0f0f0', borderRadius: '4px' }}>
                        {JSON.stringify(timestampData, null, 2)}
                    </pre>
                </div>
            )}

            {blobData && (
                <div style={{ marginBottom: '10px' }}>
                    <p>Video size: {Math.round(blobData.size / 1024)} KB</p>
                </div>
            )}

            <div style={{ marginTop: '20px' }}>
                {/* LIVE VIDEO */}
                <video 
                    className="liveVideo" 
                    ref={videoRef}  
                    style={{ 
                        display: videoURL && !recording ? 'none' : 'block',
                        width: '100%',
                        borderRadius: '8px',
                        backgroundColor: '#000'
                    }}  
                    autoPlay 
                    playsInline 
                    muted 
                />

                {/* PLAYER VIDEO */}
                {videoURL && !recording && (
                    <video 
                        className="playbackVideo" 
                        src={videoURL} 
                        controls 
                        style={{ width: '100%', borderRadius: '8px' }}
                    />
                )}

                {/* FETCHED VIDEO FROM SERVER */}
                {fetchedVideoUrl && (
                    <video 
                        className="fetchedVideo" 
                        src={fetchedVideoUrl} 
                        controls 
                        style={{ width: '100%', marginTop: '10px', borderRadius: '8px' }}
                    />
                )}
            </div>
        </div>
    );
