import { useState, useRef } from "react";

const VideoRecorder = () => {
  const [recording, setRecording] = useState(false);

  const [videoURL, setVideoURL] = useState(null);
  const [blobData, setBlobData] = useState(null);
  const [duration, setDuration] = useState(0);
  const [uploading, setUploading] = useState(false);
  const [uploadedVideoUrl, setUploadedVideoUrl] = useState(null);
  const [error, setError] = useState(null);
  const videoRef = useRef(null);
  const mediaRecorderRef = useRef(null);
  const streamRef = useRef(null);
  const chunksRef = useRef([]);
  const timerRef = useRef(null);

  const startRecording = async () => {
    try {
      setError(null);

      const stream = await navigator.mediaDevices.getUserMedia({ 
        video: true,
        audio: true
      });

      streamRef.current = stream;

      if ( videoRef.current ) {
        videoRef.current.srcObject = stream;
      }

      const mediaRecorder = new MediaRecorder(stream);
      mediaRecorderRef.current = mediaRecorder;
      chunksRef.current = [];

      mediaRecorder.ondataavailable = (event) => {
        if (event.data.size > 0) {
          chunksRef.current.push(event.data);
        }
      };

      mediaRecorder.onstop = () => {
        const blob = new Blob(chunksRef.current, { type: "video/webm" });
        setVideoURL(URL.createObjectURL(blob));
        setBlobData(blob);
        clearInterval(timerRef.current);
      };

      mediaRecorder.start(1000);
      setRecording(true);

      setDuration(0);
      timerRef.current = setInterval(() => {
        setDuration((prev) => prev + 1);
      }, 1000);
    } catch (error) {
      setError(`Camera access error: ${error.message}`);
    }
  };

  const stopRecording = () => {
    if (mediaRecorderRef.current && mediaRecorderRef.current.state !== "inactive") {
      mediaRecorderRef.current.stop();
      streamRef.current?.getTracks().forEach((track) => track.stop());
      setRecording(false);
      clearInterval(timerRef.current);
    }
  };

  const uploadToServer = async () => {
    if (!blobData) {
      setError("No video recorded yet!");
      return;
    }

    try {
      setUploading(true);
      setError(null);

      // Create form data
      const formData = new FormData();
      formData.append('video', blobData, 'recording.webm');

      console.log("Uploading file of size:", blobData.size);

      // Upload to backend
      const response = await fetch("http://localhost:8000/upload-video", {
        method: "POST",
        body: formData,
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || data.details || `Server error: ${response.status}`);
      }

      setUploadedVideoUrl(data.fileUrl);
      alert("Video uploaded successfully!");
    } catch (error) {
      console.error("Error during upload:", error);
      setError(`Upload failed: ${error.message}`);
    } finally {
      setUploading(false);
    }
  };

  return (
    <div className="p-4 max-w-lg mx-auto">

      <div className="flex gap-2 mb-4">
        <button 
          className="px-4 py-2 rounded bg-blue-500 text-white disabled:bg-gray-300"
          onClick={startRecording} 
          disabled={recording}>
          Start Recording
        </button>
        <button 
          className="px-4 py-2 rounded bg-red-500 text-white disabled:bg-gray-300"
          onClick={stopRecording} 
          disabled={!recording}>
          Stop Recording
        </button>
        <button 
          className="px-4 py-2 rounded bg-green-500 text-white disabled:bg-gray-300"
          onClick={uploadToServer} 
          disabled={!videoURL || uploading}>
          {uploading ? "Uploading..." : "Upload Video"}
        </button>
      </div>

      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 p-2 rounded mb-4">
          {error}
        </div>
      )}



      <div className="bg-gray-100 p-4 rounded">
        <h3 className="text-lg font-semibold mb-2">
          {recording ? `Recording... ${duration}s` : "Recorded Video"}
        </h3>
        <div className="aspect-video bg-black">
          <video 
            ref={videoRef} 
            autoPlay 
            playsInline 
            muted
            width="100%" 
            style={{ display: videoURL && !recording ? 'none' : 'block' }} 
          />
          {videoURL && !recording && 
            <video 
              src={videoURL} 
              controls 
              width="100%" 
            />
          }
        </div>

        {uploading && (
          <div className="mt-4">
            <p>Uploading video...</p>
          </div>
        )}

        {uploadedVideoUrl && (
          <div className="mt-4">
            <h3 className="text-lg font-semibold mb-2">Uploaded Video from S3</h3>
            <p className="text-sm text-gray-600 mb-2 break-all">{uploadedVideoUrl}</p>
            <div className="aspect-video bg-black">
              <video 
                src={uploadedVideoUrl} 
                controls 
                width="100%"
                className="mx-auto"
                onError={(e) => {
                  console.error("Video load error:", e);
                  setError("Error loading the video from S3. Check console for details.");
                }}
              />
            </div>
            <div className="mt-2">
              <a 
                href={uploadedVideoUrl} 
                target="_blank" 
                rel="noopener noreferrer"
                className="text-blue-500 underline"
              >
                Open video in new tab
              </a>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default VideoRecorder;
