import { useLocation } from "react-router-dom";
import { useState } from "react";

import RecordingVideo from "../components/RecordingVideo";

export default function Game(){
    const location = useLocation();
    const { team, players } = location.state || []; // Ensure safe destructuring
    const teamName = team.properties?.Name.title[0].plain_text;

    const [data, setData] = useState([]);

    const handleCheckboxChange = (player) => {
        setData((prevData) => {
            if (prevData.includes(player)) {
                // Remove player if already selected
                return prevData.filter((p) => p !== player);
            } else {
                // Add player if not selected
                return [...prevData, player];
            }
        });
    };

    return(
        <div className = "page">
            <h1>{ teamName }</h1>
            <p>Start Recording Game for { teamName }</p>

            {/* <VideoRecorder/> */}

            <RecordingVideo/>

            <h2>Playing</h2>
            <p className = "meta">Press the player button to timestamp highlight</p>

            {data.map((player, index) => (
                <button key={index}>{player}</button>
            ))}

            <hr/>

            <h2>Players ({ players.length })</h2>
            <p className = "meta">Choose players playing</p>
            {/* SIGN UP */}
            <div style = { { display : "flex", flexDirection : "row", gap : "8px" }}>
                { players.map( ( item, index ) => (
                    <div key = { index } style = { { width: "100px", backgroundColor : "var(--black8)", padding : "8px" } }>
                        <div style = { { height : "80px", border : "1px solid var(--black8)", backgroundPosition : "center", backgroundImage : "url(https://www.tottenhamhotspur.com/media/5y3crbxe/firstteam_profiles_mickyvandeven_202425_1.png?anchor=center&mode=crop&quality=90&width=500)", backgroundSize : "cover"} }/>
                        <p className = "number">{ item.properties.back_number.rich_text[0].plain_text }</p>
                        <p>{ item.properties?.Name.title[0].plain_text }</p>
                        <p className = "meta" style = { { whiteSpace: "normal", wordWrap: "break-word", overflowWrap: "break-word" } }>{ item.properties.first_name.rich_text[0].plain_text } { item.properties.last_name.rich_text[0].plain_text }</p>
                        <input
                        type="checkbox"
                        onChange={() => handleCheckboxChange(player)}
                        checked={data.includes(player)}
                    />

                        {/* <select style = { { width : "100%" } }>
                            <option>Playing</option>
                        </select> */}
                    </div>
                ))}
            </div>


        </div>
    )
}
